parse.code:
val userInfo = Root / "user" / Arg[String] & Param[Boolean]("show")

val result = "/user/hello?show=false" match {
  case details (a :: HNil)            => s"details: $a"
  case userInfo(u :: HNil, s :: HNil) => s"user: $u, show: $s"
}
println(result)
